vec3 gray = vec3(.5,.5,.5);

float random (in vec2 uv) {
    return fract(sin(dot(uv.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);   
}

float rect(vec2 uv, vec2 pos, vec2 size)
{
	pos -= size/2.;
    float value = step(pos.x,uv.x);
    value = (step(pos.x,uv.x) - step(pos.x+size.x,uv.x)); 
    value *= (step(pos.y, uv.y) - step(pos.y+size.y,uv.y));

    return value;
}

float circle(vec2 uv,float radius, vec2 shapePos, float width)
{

    float ratio = iResolution.x / iResolution.y;

    float dist = distance(shapePos,uv) * 3.0;
    float value =step(radius - width,dist) - step(radius, dist);

    shapePos.x *= ratio;

    return value; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	float ratio = iResolution.x / iResolution.y;

    vec2 size;
    vec2 pos;
    float value;
    vec3 color;
    float radius,width;
    vec2 shapepos;

 	color = mix(gray,color,value);

    //cross
    pos = vec2(.5 ,.5);
    size = vec2(.02,1.);
    value = 1.- rect(uv, pos,size);    
    color = mix(vec3(0,0,0),color,value);    

    pos = vec2(.5 ,.5);
    size = vec2(1,.03);
    value = 1.-rect(uv, pos,size);    
    color = mix(vec3(0,0,0),color,value); 

    //red bar
    pos = vec2(tan(iTime)/1.5,0.5);
    size = vec2(.05,1);
    value = 1.-rect(uv, pos,size);    
    color = mix(vec3(1,0,0),color,value); 

    //heartbeat1
    vec2 uv2 = uv;
    uv2.x *=ratio;
    pos = vec2(.3 + (sin(iTime) * .15),.4);
 	radius = .35;
    width = .02;
    value = 1. -circle(uv2,radius,pos,width);
    color = mix(vec3(1,1,1),color,value);

 	radius = .05 * (sin(iTime * 15.)+ .5);
    width = 1.;
    value = 1. -circle(uv2,radius,pos,width);
    color = mix(vec3(1,0,0),color,value);


    pos = vec2(1.4,0.8 + (sin(iTime) * .15));
 	radius = .35;
    width = .02;
    value = 1. -circle(uv2,radius,pos,width);
    color = mix(vec3(1,1,1),color,value);

 	radius = .05 * (sin(iTime * 15.)+ .5);
    width = 1.;
    value = 1. -circle(uv2,radius,pos,width);
    color = mix(vec3(1,0,0),color,value);


    value = random(uv + iTime) * 1.5;
    color = mix(vec3(1,1,1),color,value);



    fragColor = vec4(color,1.0);
}
