void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 col = texture(iChannel0, uv);
    col = pow(col, vec4(3));
    fragColor = col*0.005 + texture(iChannel1, uv);

    if( iMouse.z > 0. )
        fragColor = vec4(0.);
}
