void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 c_t1 = texture( iChannel0, uv );
    vec4 c_t0 = texture( iChannel1, uv );
    float diff = abs(c_t0.r - c_t1.r) + abs(c_t0.g - c_t1.g) + abs(c_t0.b - c_t1.b);
	fragColor = vec4(smoothstep(0.25, 0.15, diff));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 c_t1 = texture( iChannel0, uv );   
    vec4 c_t0 = texture( iChannel1, uv );

	fragColor = fract(iTime*0.25)>0.02?c_t1:c_t0;
}
