#define OCTAVES 7
#define SCALE 0.55

vec2 random(vec2 uv)
{
    return texture(iChannel3, uv).xy;
}

vec2 vn(vec2 uv, float persistence)
{
    vec2 v = vec2(0.0);
    float m = 0.0;
    for (int i=0; i<OCTAVES; ++i)
    {
        v = v*persistence+random(uv);
        m = m*persistence+1.0;
        uv *= SCALE;
    }
    return v/m;
}

vec2 texture_(vec2 uv, float q, float p)
{
    vec2 t = vec2(q, 0.0);
    return vn(uv+t.yx+0.2*vn(uv-t, p), p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv+0.2*texture_(uv, iTime*0.01, 0.3));
}
