void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = fragCoord.xy/iResolution.xy;

    vec3 color = vec3(st.y * sin(iTime));

    float v = (cos((st.x * 10.0) + iTime * 1.0) + cos((st.x * 10.0)*2.0/3.0*3.1415 + iTime * 1.0) )/5.0 + 0.5;
    float v2 = v +  sin((st.x * 20.0)*100.0*3.1415 + iTime * 1.0) / 10.0  ;

    float v3 = (cos((st.x*sin(st.y) * 10.0) + iTime * 1.0) + cos((st.x * 10.0)*2.0/3.0*3.1415 + iTime * 1.0) )/5.0 + 0.5;

    float v4 = 0.25+ 0.7/(exp((st.x+(sin(iTime)/2.0)-0.75)/0.07)+1.0);
    float v5 =  0.75- 0.7/(exp((st.x+(sin(iTime)/2.0)-0.25)/0.07)+1.0);

    float v6 = (v4*v5+0.3);
    float v7 = 0.6 + 0.5/(exp((st.x+(sin(iTime)/2.0)-0.45)/0.01)+1.0)- 0.5/(exp((st.x+(sin(iTime)/2.0)-0.55)/0.01)+1.0);

    float v8 = v7*(0.5+0.4*sin(st.x*iTime));

    float pct = plot(st ,v8);


    color = (pct)*color+pct*vec3(1.0,0.0,0.0);

fragColor = vec4(color,1.0);
