void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Color
    vec3 u_color = vec3(1.0, 1.0, 1.0);

    //Texture
	vec2 i_uv = fragCoord.xy / iResolution.xy;
	vec3 textureColor = texture(iChannel0, i_uv).xyz;

    //Ambient Light
    float u_ambientStrength = 0.2;
    vec3 ambientColor = u_color * u_ambientStrength;

    //Diffuse Light
    vec3 u_lightColor = vec3(1.0, 1.0, 1.0);
    vec3 i_lightDirection = vec3(1.0, 1.0, 1.0);
    vec3 i_normalDirection = vec3(1.0, 1.0, 1.0);
    vec3 lightDirection = normalize(i_lightDirection);
    vec3 normalDirection = normalize(i_normalDirection);
	vec3 diffuseColor = u_lightColor * max(dot(normalDirection, lightDirection), 0.0);

    //Final
    fragColor = vec4(textureColor * (ambientColor + diffuseColor), 1.0);
}
