//Scret(Sound Image) by 834144373 is licensed under a cc3.0 cc-by-nc-sa Creative Commons License.
void mainImage( out vec4 c, in vec2 u )
{
    vec2 uv = u/iResolution.xy;
    c = texture(iChannel0,uv);
}

//---------------
//This audio information: 
//sampleRate:44.1KHz
//frequency: 0Hz-11025Hz
//---------------
//-----------------
//shadertoy info:
//fftSize:2048
//frequencyBinCount:1024
//smoothingTimeConstant: 0.8
//frequencyData length :1024
//waveData length:1024
//audio buffer length:512
//-----------------
/*
1.	don't store frequency at (sampleRate/2,sampleRate/4)
	because shadertoy store Frequency Domain length at 512,(but the default is 2048/2)
	and shadertoy's sampleRate info is worry(it allways is 48000).
2.  don't direct use the "TimeDomainData",because soundcloud change the data.
*/
void mainImage( out vec4 c, in vec2 u )
{
    vec3 R = iResolution.xyz;
    u /= R.xy;
    vec2 uv = u;
    c = texture(iChannel1,uv);
    	uv = ceil(uv*vec2(800.,1.));
    if(uv.x == float(iFrame))
    	c = texture(iChannel0,vec2(u.y*0.858,0.17)).xxxx;

}
