void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 p = uv*2.0 - 1.0;
    float as = iResolution.y/iResolution.x;

    vec3 col1 = texture(iChannel0,uv).rgb;
    vec3 col2 = texture(iChannel1,uv).rgb;

    float g = sin(iTime)*2.2;

    float a = clamp(cos(p.x*200.0)-cos(p.y*200.0*as)+g+1.0,0.0,1.0);
    a = pow(a,10.0);
    vec3 col = mix(col1,col2,a);
    // Output to screen
    fragColor = vec4(col,1.0);
}
