const float sharpness = 100.0;

vec2 uvmap(vec2 pixels)
{
    vec2 uv = pixels / iResolution.xy * 2.0 - 1.0;
    return uv * 10.0;
}

float line(vec2 uv, vec2 p1, vec2 p2)
{
    vec2 a = uv-p1;
    vec2 b = uv-p2;
    vec2 c = p2-p1;
    float aa=dot(a,a), bb=dot(b,b), cc=dot(c,c);
    float fx = aa+bb-cc+2.0*sqrt(aa*bb);
    return clamp(1.0-fx*sharpness, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = uvmap(fragCoord.xy);

    vec2 p1 = vec2(0), p2 = uvmap(iMouse.xy);

    vec3 col = vec3(line(uv, p1, p2));

    fragColor = vec4(col,1.0);
}
