// subset of https://shadertoy.com/view/Ms3yD7
// inverse of https://shadertoy.com/view/4scyWM

void mainImage(out vec4 O, vec2 U) { O = texture( iChannel0, U /  iResolution.xy ); }

const int Z = 5;                   // recursion depth

#define rot(a)     mat2( cos(a), -sin(a), sin(a), cos(a) )

void mainImage( out vec4 O, vec2 u ) {
    vec2 R = iResolution.xy,
         U = 1.2 * ( u+u - R ) / R.y, 
         M = iMouse.xy / R, z;

    float T = .5*iTime, k=.95;
    if ( length(iMouse.xy)<10. )      // auto-demo
        M = .5 + .4 *vec2( cos(T)-.4*sin(2.7*T), sin(1.73*T)-.3*cos(2.3*T) ) / 1.4,
        k = .98;                      // relaxation duration

    float s = (.5+.5*M.x)/2.,         // scaling
          t = 1.-s,                   // translation
          a = 3.14*M.y;               // rotation
    O = k * texture(iChannel0, u/R) * vec4( .96, .98, 1, 0); // relaxation


    z = U;
    for( int i=0; i<Z; i++ ) { // --- iterate up to depth N. should be log(1/R.y)/log(s)
        z = abs(z);
        z = (z-vec2(t,t))* rot(-a) / s;
    }

    O += smoothstep(.01,0.,length(z)-1.) ;
    O = clamp(O, 0., 1.);
}
