void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 CHANNEL_CENTER_0 = vec2(0.05*cos(iTime*2.0), 0.0);
vec2 CHANNEL_CENTER_1 = vec2(0.0, 0.05*cos(iTime*1.5));
vec2 CHANNEL_CENTER_2 = vec2(0.0, 0.0);

	vec2 uv = fragCoord.xy / iResolution.xy;
	//uv.y = 1.0 - uv.y;

	vec2 uvCenter = uv*2.0 - 1.0;


const float k1 = -0.12;
const float k2 = -0.02;

	vec3 color = vec3(0);

	// CHANNEL0
	vec2 d0 = CHANNEL_CENTER_0 - uvCenter;
	float d2 = dot(d0, d0);
	float d4 = d2 * d2;

	vec2 uv0 = uvCenter / vec2(1.0 + k1 * d2 + k2 * d4);
	color[0] = texture(iChannel0, uv0*0.5+0.5)[0];

	// CHANNEL1
	d0 = CHANNEL_CENTER_1 - uvCenter;
	d2 = dot(d0, d0);
	d4 = d2 * d2;

	vec2 uv1 = uvCenter / vec2(1.0 + k1 * d2 + k2 * d4);
	color[1] = texture(iChannel0, uv1*0.5+0.5)[1];

	// CHANNEL2
	d0 = CHANNEL_CENTER_2 - uvCenter;
	d2 = dot(d0, d0);
	d4 = d2 * d2;

	vec2 uv2 = uvCenter / vec2(1.0 + k1 * d2 + k2 * d4);
	color[2] = texture(iChannel0, uv2*0.5+0.5)[2];



	fragColor = vec4(color, 1);
}
