void mainImage( out vec4 f, vec2 g )
{
	f = texelFetch(iChannel0, ivec2(g),0); // thanks to dave hoskins
}

void mainImage( out vec4 f, vec2 g )
{
    f.xyz = iResolution;
    vec2 v = (g+g-f.xy)/f.y,k;
    f *= texture(iChannel0, g/f.xy) / length(f);
    for(g-=g;g.x<6.3;g+=.01)
        f += 1e-5 / dot(
            k = v - sin(g + vec2(1.6,0)) * fract(iTime*.1+4.*sin(g*6.))*3.,
            k);
}
