const float maxit = 32.0;
const float zoomspeed = 0.7;
const float zoommin = 2.4;
const float zoommax = 3.5;
const float colorspeed1 = 0.25;
const float colorspeed2 = 0.15;
const float rotspeed = 0.33;
const float audioint = 4.0;

/*
* By Kivibot
*/
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 position = ( fragCoord.xy / iResolution.xy ) + iMouse.xy/ iResolution.xy - vec2(1.0,1.0);
	position.x *= iResolution.x/iResolution.y;

	float rs = sin(iTime*rotspeed);
	float rc = cos(iTime*rotspeed);

	//magical rotating with complex numbers

	float tmpx = rc*position.x-rs*position.y;
	position.y = rc*position.y+position.x*rs;
	position.x = tmpx;

	//basic zooming

	position *= zoommin + sin(iTime*zoomspeed)*(zoommax-zoommin);

	//escape algorithm

	vec2 a = vec2(0,0);
	vec2 c = position;

	float col = 0.0;

	for(float i=0.0; i<maxit; i++){
		if(col != 0.0){
		} else if(a.x*a.x + a.y*a.y < 4.0){
			float a_x = a.x*a.x-a.y*a.y+c.x;
			a.y = 2.0*a.x*a.y+c.y;
			a.x = a_x;
		} else{
			col = i/maxit;
		}
	}

	//coloring

	float cm = audioint*(texture( iChannel0, vec2( 0.02, 0.26 ) ).x-0.7);


	float cs = (0.93+cm)*(0.35+abs(sin(iTime*colorspeed1)));
	float cc = (0.93+cm)*(0.35+abs(cos(iTime*colorspeed2)));

	fragColor = vec4( cs*cc*col, cs*col, cc*col, 1.0 );

}
