// (x,y) -> (r,g,b)
// Note this will only work while L < all Y components.

const float L = 0.7;

const float Yr = 0.3;
const float Yg = 0.59;
const float Yb = 0.11;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 plane = fragCoord.xy / iResolution.xy;

    // Map y directly to b
    float b = plane.y;
    float LSubB = L - 0.11 * b;

    // Use b value to determine r and g

    // Max out r when x = 0.
    float rAt0 = 1.0;
    float LSubBR = LSubB - Yr;
    float gAt0 = LSubBR / Yg;

    // Max out g when x = 1.
    float gAt1 = clamp(LSubB / Yg, 0.0, 1.0);
    float LSubBG = LSubB - Yg;
    float rAt1 = LSubBG / Yr;

    // r = xAt0 when x=0, g = xAt1 when x=1, interpolate in between.
    float r = plane.x * rAt1 + (1.0 - plane.x) * rAt0;
    float g = plane.x * gAt1 + (1.0 - plane.x) * gAt0;

    fragColor = vec4(r,g,b,1);
}
