void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = -1.0 + 2.0*fragCoord.xy / iResolution.xy;
	uv.x;// *=  iResolution.x / iResolution.y;
    uv.y;// *=  iResolution.x / iResolution.y;

    // background	 
	vec3 color = vec3(0.8 + 0.2*uv.y);
    vec3 s = texture(iChannel0, vec2((.25*(uv.x*.5+.5)), 0.11)).xyz;        
    float f = s.r;
    vec3 str = vec3(1,1,1);
    if(uv.y > f)
        str *= 0.0;

    color = vec3(0,0,0); 

	for( int i=0; i<30; i++ )
	{

		float pha =      sin(float(i)*546.13+1.0)*0.5 + 0.5;
		float siz = pow( sin(float(i)*651.74+5.0)*0.5 + 0.5, 5.0 + uv.x *.02) * 1.00;// * s.r;
		float pox =      cos(float(i)*321.55+4.1) * iResolution.x / iResolution.y;

        // size, position and color
		float rad = 0.1 + 0.5*siz;
		vec2  pos = vec2( pox, -1.0-rad + (2.0+2.0*rad)*mod(pha+0.1*iTime*(0.2+0.8*siz),1.0));
        //pos += vec2(sin(float(i)/10.0),0.0);
   		float dis = length( uv - pos )*1.0;// - 1.1*s.r;

        vec3 s = texture(iChannel0, vec2((.25*(pos.x*.5+.5)), 0.11)).xyz;    

        float f = dot(normalize(uv - pos), vec2(1.0,0.0));
        f = abs(f); 
        f *= .25;

        s = texture(iChannel0, vec2(f, 0.11)).xyz;    
        s -= f * 3.0;               
        s = s * .5 + .5;

        vec3 color1 = vec3(1,.1,1);
        vec3 color2 = vec3(0,.1,1);

        vec3 newColor = mix(color1, color2, dis* .2 + s.r * 1.3 - .5);
        //vec3 newColor = mix(color1, color2, dis* 5.0 - s.r * .3);

        if(dis < s.r * .6)
            color += newColor;         
	}

    color = clamp(color,0.0,1.0);
	color *= sqrt(1.5-0.5*length(uv));

	fragColor = vec4(color,1.0);
}
