// chnage this to change pixel resolution
// 1 is normal
// 5 is 5 times pixelation
float resolutionScale = 3.0;

float lerp(float numb1, float numb2, float t)
{
	  return numb1 * t + numb2 * (1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 pic = texture(iChannel0, fragCoord/iResolution.xy);
	float r = lerp((mod(fragCoord.x,resolutionScale)) * pic.r,pic.r,0.5);
    float g = lerp((mod(fragCoord.y,resolutionScale))* pic.g,pic.g,0.5);
    float differance = lerp((pic.r - r),(pic.g -g),0.5);
    float b = pic.b - differance; 
	fragColor = vec4(r,g,b,1.0);
}
