//the distance field, change the number to change the shadow hardness
#define m dot(sin(p),vec3(2))

void mainImage( out vec4 f, in vec2 u )
{
    u = (u+iMouse.xy*3.)/iResolution.y;
    vec3 p = vec3(1,iTime,1);
    vec3 d = vec3(sin(u.x+p.y*.5+vec2(0,1.6)),u.y-2.);
    d /= abs(d.y);
    p += d*fract(-p.y*d.y);

    //raymarching
    for (int i = 0; i < 100; i++)
    {
        if (m > 0.) break;
    	p+=d;

    }
    //shading, texture+simple shadow
    p.y-=d.y;
    f.xyz = clamp(m+.5,.3,1.)*(sin(p)*.5+.5);//*texture(iChannel0,p.xz);
}
