/**


#define m(p) dot(sin(p),vec3(2))

void mainImage( out vec4 f, in vec2 g )
{
	vec2 u = (g*2.-iResolution.xy) / iResolution.y;

    vec3 p = vec3(1,1,iTime);
    p += vec3(u,1)*(1.-fract(p.z));

    while (m(p) > 0.) {
        p += vec3(u,1);
    }

    f = clamp(m(p-vec3(0,0,1)),.3,1.)*texture(iChannel0,p.xy);
}




