#define VERT false

float fromRgb( vec3 v ) {
    return ( ( v.z * 256.0 + v.y ) * 256.0 + v.x ) * 255.0;
}

vec4 draw( vec2 uv ) {
    //return texture( iChannel0, uv );

    vec2 dirVec = VERT ? vec2( 0.0, 1.0 ) : vec2( 1.0, 0.0 );
    float wid = VERT ? iResolution.y : iResolution.x;
    float pos = VERT ? floor( uv.y * iResolution.y ) : floor( uv.x * iResolution.x );

    for ( int i = 0; i < int( wid ); i ++ ) {
        vec2 p = uv + dirVec * float( i ) / wid;
        if ( p.x < 1.0 && p.y < 1.0 ) {
            float v = fromRgb( texture( iChannel0, p ).xyz );
            if ( abs( v - pos ) < 0.5 ) {
                return texture( iChannel1, p );
                break;
            }
        }

        p = uv - dirVec * float( i ) / wid;
        if ( 0.0 < p.x && 0.0 < p.y ) {
            float v = fromRgb( texture( iChannel0, p ).xyz );
            if ( abs( v - pos ) < 0.5 ) {
                return texture( iChannel1, p );
                break;
            }
        }
    }

    return vec4( 1.0, 0.0, 1.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    fragColor = draw( fragCoord / iResolution.xy );
}

#define VERT false
#define THR ( iMouse.x < 1.0 ? ( sin( iTime * 5.0 ) * 0.5 + 0.5 ) : iMouse.x / iResolution.x )
#define SHADOW false
#define REVERSE false

float gray( vec3 c ) {
    return dot( c, vec3( 0.299, 0.587, 0.114 ) );
}

vec3 toRgb( float i ) {
    return vec3(
        mod( i, 256.0 ),
        mod( floor( i / 256.0 ), 256.0 ),
        floor( i / 65536.0 )
    ) / 255.0;
}

bool thr( float v ) {
    return SHADOW ? ( THR < v ) : ( v < THR );
}

vec4 draw( vec2 uv ) {
    vec2 dirVec = VERT ? vec2( 0.0, 1.0 ) : vec2( 1.0, 0.0 );
    float wid = VERT ? iResolution.y : iResolution.x;
    float pos = VERT ? floor( uv.y * iResolution.y ) : floor( uv.x * iResolution.x );

    float val = gray( texture( iChannel0, uv ).xyz );

    if ( !thr( val ) ) {
        float post = pos;
        float rank = 0.0;
        float head = 0.0;
        float tail = 0.0;

        for ( int i = 0; i < int( wid ); i ++ ) {
            post -= 1.0;
            if ( post == -1.0 ) { head = post + 1.0; break; }
            vec2 p = dirVec * ( post + 0.5 ) / wid + dirVec.yx * uv;
            float v = gray( texture( iChannel0, p ).xyz );
            if ( thr( v ) ) { head = post + 1.0; break; }
            if ( v <= val ) { rank += 1.0; }
        }

        post = pos;
        for ( int i = 0; i < int( wid ); i ++ ) {
            post += 1.0;
            if ( wid == post ) { tail = post - 1.0; break; }
            vec2 p = dirVec * ( post + 0.5 ) / wid + dirVec.yx * uv;
            float v = gray( texture( iChannel0, p ).xyz );
            if ( thr( v ) ) { tail = post - 1.0; break; }
            if ( v < val ) { rank += 1.0; }
        }

        pos = REVERSE ? ( tail - rank ) : ( head + rank );
    }

    return vec4( toRgb( pos ), 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    fragColor = draw( fragCoord / iResolution.xy );
}
