#define t iTime * 5.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color;

    if(uv.y*(3. + sin(t)) >= sin(uv.x*(20. + sin(t)) + -t) + 1.5 && uv.y*(3. + sin(t)) <= sin(uv.x*(20. + sin(t)) + -t) + 1.6)
    	color = vec4(1.,0.,0.,1.);
    else
    	color = vec4(0.,0.,0.,1.);

	fragColor = color;
}
