void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

#define PI acos(-1.0)
#define PI2 2.*PI

int sn = 5; // Samples count
float sr =50.; // Sampling distance

vec3 hsv2rgb(vec3 c) { 
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0); 
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www); 
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0/3.0, 2.0/3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

float nrand(vec2 co)
{
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float rand(vec2 n)
{
	float nrnd0 = nrand( n + 0.07*fract( iTime ) );
	float nrnd1 = nrand( n + 0.128*fract( iTime + 0.573953 ) );
	return 0.23*sqrt(-log(nrnd0+0.00001))*cos(2.0*3.141592*nrnd1)+0.5;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
 float mxn = max(iResolution.x, iResolution.y);
 vec2 pst = sr/iResolution.xy;
 vec2 uvn = (fragCoord.xy - iResolution.xy*0.5)/mxn;
 vec2 mx = iResolution.xy;
 vec2 uv = fragCoord.xy/mx;
 vec3 pc = vec3(0.0);
 if (nrand(vec2(uv*(iTime+100.)))<.0000000000000000001)
 {
  pc = 0.5*(0.3*pc+0.7*hsv2rgb(vec3((1.0, 1.0, nrand(33.33*uv.yx*iTime)))));
 }
 else
 {
  vec2 aov = vec2(.0);
  float lacc = 0.;
  float d = PI2*rand(uv+7.48*iTime/180.);
  for(int i=0; i<sn; i++)
  {
   float a = PI2*(d+float(i))/float(sn);
   vec2 sov = pst*vec2(cos(a), sin(a));
   float sl = rgb2hsv((texture(iChannel0, uv + sov).rgb)).z;
   aov += sl*sov;
   lacc += sl;
  }
  aov = aov/lacc/float(sn);
  pc = texture(iChannel0, uv + aov).rgb;
  vec3 rcol = hsv2rgb(vec3(1.0+9.9*rand(uv*sin(iTime)), 0.9, .78));
  pc = pc*(vec3(.73)+vec3(.62+0.03*sin(iTime*0.3), .62+0.03*sin(0.2+iTime*0.4), .62+0.03*sin(0.5+iTime*0.5))*rcol);
 }
 fragColor = vec4(pc, 1.0);
}
