float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 offset(float t){
 	return vec2(sin(t),cos(t*0.78)+sin(t*2.11)*0.2);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy*4.;
    float blur=iMouse.x/iResolution.x*3.;
    vec2 os = offset(iTime);
    vec2 mb=(os-offset(iTime-0.1))*iMouse.y/iResolution.y*0.5;//.yx;
	fragColor = (
        +texture(iChannel0,uv+os+mb*rand(gl_FragCoord.xy+5.4363+iTime)+(vec2(rand(gl_FragCoord.xy+66.),rand(gl_FragCoord.xy+10.1123))-0.5)*blur*0.01)
        +texture(iChannel0,uv+os+mb*rand(gl_FragCoord.xy+4.5433+iTime*0.2)+(vec2(rand(gl_FragCoord.xy+6.57),rand(gl_FragCoord.xy+18.123))-0.5)*blur*0.01)
        +texture(iChannel0,uv+os+mb*rand(gl_FragCoord.xy+7.1133+iTime*75.)+(vec2(rand(gl_FragCoord.xy+1.1123),rand(gl_FragCoord.xy+7.223))-0.5)*blur*0.01)
        +texture(iChannel0,uv+os+mb*rand(gl_FragCoord.xy+12.3313+iTime*6.131)+(vec2(rand(gl_FragCoord.xy+3.53),rand(gl_FragCoord.xy+1.78563))-0.5)*blur*0.015)
        +texture(iChannel0,uv+os+mb*rand(gl_FragCoord.xy+5.35343+iTime*7.654)+(vec2(rand(gl_FragCoord.xy-24.566),rand(gl_FragCoord.xy+4.83))-0.5)*blur*0.02)
    )/5.;
}
