//If the define is commented, the image is converted to lineal.
//If the define is uncommented, the image is converted to gamma.

//#define GAMMA;


float gamma = 2.2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
  	fragColor = vec4(texture(iChannel0,uv).xyz,1.0);
  	vec3 textureColor = vec3(texture(iChannel0,uv).xyz);

 	#ifdef GAMMA	
   		 vec3 finalColor = (pow(textureColor, vec3(gamma)));
	#else
 		 vec3 finalColor = (pow(textureColor, vec3(1.0/gamma)));
	#endif

    fragColor = vec4(finalColor,1.0/2.2);

}
