renderpass.code:
struct Rect {
	vec2 pos,
         ext;
    mat2 kord;
    vec4 col;

};


Rect D1 = Rect ( vec2(-.45+.5*cos(iTime),-.6+.5*sin(iTime)), vec2 (1.23,1.23), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R1 = Rect ( vec2(-.2+.5*cos(iTime),.2+.5*sin(iTime)), vec2 (.6,.6), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R2 = Rect ( vec2(-.2,.2), vec2 (.6,.6), mat2 (1.,0.,0.,1.), vec4 (9,.99,.80,2.));





mat2 Rota (in float alpha){
	float s = sin(alpha),
          c = cos(alpha);

   return mat2 (c, -s, s, c);

}

void Rotarect (in float alpha, inout Rect r_p){

    r_p.kord = Rota (alpha) * r_p.kord;


}





vec4 intersec (in vec2 p_uv, in Rect reco){
	vec2 xynew = p_uv - reco.pos;
    vec2 mat = reco.kord * xynew;

    if ( abs(mat.x) >.5*reco.ext.x && abs(mat.y) >.5*reco.ext.y){
        return vec4 (0.);
    	}
        if (mat.x*mat.x+mat.y*mat.y <.135 && mat.y > -0.2)
        return reco.col;

    return vec4(.0);

}

vec4 intersecD (in vec2 p_uv, in Rect reco){
	vec2 xynew = p_uv - reco.pos;
    vec2 mat = reco.kord * xynew;

    if ( abs(mat.x) >.5*reco.ext.x || abs(mat.y) >.5*reco.ext.y){
        return vec4 (0.);
    	}
        if (abs(1.65*mat.x)*reco.ext.x + mat.y*reco.ext.y >.4)
        return vec4(.0);

    return reco.col;

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy / iResolution.xy-.5)*(iResolution.x/iResolution.y,1.);
	fragColor = vec4(.5,.25,.75,.1);

	Rotarect (iTime, R1);
    Rotarect (iTime, R2);
    Rotarect (iTime+3.14, D1);



    vec4 r1col = intersec (uv,R1);
    vec4 r2col = intersec (uv,R2);
	vec4 r3col = intersecD (uv,D1);
    float col = r1col.a + r2col.a +r3col.a;

    if (col > 3.){
        fragColor = vec4(.9,.99,.80,4.); }

    else

    	if (col >1. && col <3.)

        fragColor = vec4(.9,.99,.80,4.);

   		 else
        fragColor = vec4(.5,.25,.75,.1);



}
