void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy;
    vec3 texColor = texture(iChannel0,xy).xyz;
    vec3 bw = vec3(dot(texColor, vec3(0.2126, 0.7152, 0.0722)));
    fragColor = vec4(mix(bw, texColor, abs(cos(iTime * 0.5))), 1.0);
}
