#define WIDTH 16
#define HEIGHT 16
#define PEN_WIDTH_X 2.
#define PEN_WIDTH_Y 2.

float random (vec2 co) {
    return fract (sin (dot (co.xy ,vec2 (12.9898,78.233))) * 43758.5453);
}

struct World {
    vec4 cells[WIDTH * HEIGHT];
    int generation;
};

vec2 getTileSize () {
    return vec2 (iResolution.x / float (WIDTH), iResolution.y / float (HEIGHT));
}

vec2 getTilePosition (vec2 fragCoord) {
    vec2 size = getTileSize ();
    return vec2 (floor (fragCoord.x / size.x), floor (fragCoord.y / size.y));
}

bool isGridLine (vec2 fragCoord) {    
    vec2 size = getTileSize ();
    vec2 tile = getTilePosition (fragCoord);    
    vec2 penWidthHalf = vec2 (PEN_WIDTH_X / 2., PEN_WIDTH_Y / 2.);

    float leftStart = clamp (size.x * tile.x - penWidthHalf.x, 0., iResolution.x);
    float leftEnd = size.x * tile.x + penWidthHalf.x;

    float rightStart = size.x * (tile.x + 1.) - penWidthHalf.x;
    float rightEnd = clamp (size.x * (tile.x + 1.) + penWidthHalf.x, 0., iResolution.x);

    float upStart = clamp (size.y * tile.y - penWidthHalf.y, 0., iResolution.y);
    float upEnd = size.y * tile.y + penWidthHalf.y;

    float downStart = size.y * (tile.y + 1.) - penWidthHalf.y;
    float downEnd = clamp (size.y * (tile.y + 1.) + penWidthHalf.y, 0., iResolution.y);


	return (leftStart <= fragCoord.x && fragCoord.x <= leftEnd)
        || (rightStart <= fragCoord.x && fragCoord.x <= rightEnd)
        || (upStart <= fragCoord.y && fragCoord.y <= upEnd)
        || (downStart <= fragCoord.y && fragCoord.y <= downEnd)
        ;    
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;;

	fragColor = isGridLine (fragCoord) 
        ? vec4 (1, 1, 0.6, 1)
        : vec4 (0, 0, 0, 0)
        ;
}
