float sampleLightY(sampler2D channel, vec2 fragCoord)
{
    float light = 0.0;
#   define S(DX, DY, WEIGHT) light += texture(channel, (fragCoord+vec2(DX, DY))/iResolution.xy).y*WEIGHT;
    S( 0,  1, -0.2)
    S( 0, -1,  0.2)
    S( 1,  0,  0.2)
    S(-1,  0, -0.2)
#   undef S
    return light;
}

vec3 hsv2rgb(vec3 hsv)
{
    vec3 rgb;
    float h = mod(hsv.x * 6.0, 6.0);
    float q = h-float(int(h));
    if      (h < 1.0) rgb = vec3( 1.0,    q,  0.0);
    else if (h < 2.0) rgb = vec3(1.-q,  1.0,  0.0);
    else if (h < 3.0) rgb = vec3( 0.0,  1.0,    q);
    else if (h < 4.0) rgb = vec3( 0.0, 1.-q,  1.0);
    else if (h < 5.0) rgb = vec3(   q,  0.0,  1.0);
    else if (h < 6.0) rgb = vec3( 1.0,  0.0, 1.-q);
    rgb = hsv.z*(1.0-hsv.y*(1.0-rgb));
    return rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float uValue = texture(iChannel0, uv).x;
    float light = sampleLightY(iChannel0, fragCoord)*8.0;
	fragColor = vec4(hsv2rgb(vec3(1.0-uValue*0.8-0.16,
                                  light > 0.0 ? 1.-light : 1.,
                                  light > 0.0 ? 1. : 1.+light)), 1.0);
}

#define DV 0.25 /* scale */
#define F 0.078 /* feed parameter, see http://mrob.com/pub/comp/xmorphia/ */
#define K 0.061 /* kill parameter, see http://mrob.com/pub/comp/xmorphia/ */
#define SPEED 0.5 /* timestep */

vec2 random(vec2 uv)
{
    return texture(iChannel3, uv).xy;
}

#define S(DX, DY, WEIGHT) laplacian += texture(channel, (fragCoord+vec2(DX, DY))/iResolution.xy).xy*WEIGHT;
vec2 laplacian(sampler2D channel, vec2 fragCoord)
{
    vec2 laplacian = vec2(0);
    S( 0,  0, -1.0)
    S( 0,  1,  0.2)
    S( 0, -1,  0.2)
    S( 1,  0,  0.2)
    S(-1,  0,  0.2)
    S( 1,  1,  0.05)
    S( 1, -1,  0.05)
    S(-1,  1,  0.05)
    S(-1, -1,  0.05)
    return laplacian;
}
#undef S


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 prevColor = texture(iChannel0, uv);
    float u = prevColor.x;
    float v = prevColor.y;
    if (iFrame == 1)
    {
        vec2 rand = random(uv);
        u = rand.x;
        v = rand.y;
    }
    else
    {
        float rate = u * v * v;
        vec2 diffusion = laplacian(iChannel0, fragCoord);
        float deltaU = diffusion.x*DV*2. - rate + F*(1.0-u);
        float deltaV = diffusion.y*DV*1. + rate - v*(F+K);
        float g = // mouse
            0.9 - distance(iMouse.xy, fragCoord)*0.1;
        if (g > v) v = g;
        float dt = SPEED;
        u += deltaU * dt;
        v += deltaV * dt;
    }
    fragColor = vec4(u, v, 0.0, 0.0);
}

