void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 vPos = vec2(0.001643721971153, -0.822467633298876) + ( ((fragCoord.xy / iResolution.yy) - vec2(0.5, 0.5)) ) * exp(2.0-iTime);

    vec3 z = vec3(0,0,0);
    int iteration; 
    fragColor = vec4(0,0,0,1);
    for(int i=0; i<1000; i++) {
        z = vec3(z.x*z.x - z.y*z.y + vPos.x, 2.0*z.y*z.x + vPos.y, 0);
        if (z.x*z.x + z.y*z.y > 4.0) {
            fragColor = vec4(1,1.0-(float(i)/100.0),1.0-(float(i)/100.0),1);
            break;
        }
    }
}
