// thanks, coyote :D
void mainImage(out vec4 f, vec2 C)
{
	C += C - (f.xy = iResolution.xy);
    float L = 10. * length(C /= f.y),                            // 10 * radius
          a = max(L - 5.3, 0.) + max(4.2 - L - L, 0.),           // circles
          b = 2. * fract(2.546 * atan(C.x, C.y) - iDate.w) - 1.; // bands

    f = L < 6.7 && a*a + b*b > .5 ? vec4(.97, 0, 0, 1)	         // red
        : vec4( 0, .5, C.y > 0., 1 );                            // blue and green    
}

