#define PI 3.14159265359
#define E 2.7182818284

float saw(float x)
{
    return acos(cos(x))/3.14;
}
vec2 saw(vec2 x)
{
    return acos(cos(x))/3.14;
}

vec2 spiral(vec2 uv)
{
    float turns = 4.0;
    float r = pow(log(length(uv)+1.), 1.175);
    float theta = atan(uv.y, uv.x)*turns-r*PI;
    return vec2(saw(r*PI+theta/turns+iTime), saw(theta+iTime*1.1));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float scale = 2.0*PI;
    uv *= scale;
    uv -= scale/2.0;
	uv.x *= iResolution.x/iResolution.y;
    uv += vec2(cos(iTime*.234), sin(iTime*.345))*1.0;
    uv = spiral(uv*scale);
    uv = spiral(uv*scale);
    uv = spiral(uv*scale);
    uv = spiral(uv*scale);
   fragColor = vec4(texture(iChannel0, saw(uv*1.0*PI)));
}
