void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float duration = 2.0;

    // time  val
    // 0     1
    // 1     1.999...
    float t = iTime;
    float v = 1.0 + mod( t, duration ); // Interval is [Closed,Open): [0,1)

    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture( iChannel0, uv );
    fragColor.xyz *= v;
}
