//tutorial:  http://gamedevelopment.tutsplus.com/tutorials/a-beginners-guide-to-coding-graphics-shaders--cms-23313

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// vec2 uv = fragCoord.xy / iResolution.xy;
	// fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    // vec4 solidGray = vec4(0.5,0.5,0.5,1.0);
    // vec4 solidRed = vec4(1.0,0.0,0.0,1.0);

    vec2 xy = fragCoord.xy; // get xy for current pixel

    vec4 gradientBluePurple = vec4(0.0,0.0,1.0,1.0); // blue

    xy.x = xy.x / iResolution.x; //x is 0 for leftmost pixel and 1 for rightmost pixel
    xy.y = xy.y / iResolution.y;

    gradientBluePurple.r = (xy.x + xy.y) / 2.0;; // set its red component to normalized x value

    fragColor = gradientBluePurple;

}
