#define D 128
#define V 3.

vec3 hsv2rgb(float h, float s, float v)
{
    h = fract(h);
    vec3 c = smoothstep(2./6., 1./6., abs(h - vec3(0.5, 2./6., 4./6.)));
    c.r = 1.-c.r;
    return mix(vec3(s), vec3(1.0), c) * v;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord/iResolution.xy) * V - V * 0.5;
    float j = 0.;
    vec2 c = iMouse.xy/iResolution.xy;
    c = mix(c, vec2(cos(iTime) * 0.25 + 0.5, sin(iTime) * 0.25 + 0.5), step(0.0,-1. *length(c)));
    c = c * 2. - 1.;
    for (int i = 0;i<D;++i)
    {
        j = j+1.;
        p = mat2(p, -p.y, p.x) * p + c;
        if (length(p) > 4.)
            break;
    }
    j = j / float(D);
    fragColor = vec4(hsv2rgb(j, 0.6, 1.0), 0.);
}
