//From https://github.com/hughsk/glsl-hsv2rgb
vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 textel = vec4(texture(iChannel0, fragCoord / iResolution.xy));
	fragColor = vec4(hsv2rgb(vec3(textel.g, textel.r * vec2(0.2, 0.8) + vec2(0.8, 0.2))), 1);
}

const float pi = 3.14159265359;

//Hash functions from https://www.shadertoy.com/view/4djSRW
#define HASHSCALE1 .1031
#define HASHSCALE3 vec3(.1031, .1030, .0973)
#define HASHSCALE4 vec4(1031, .1030, .0973, .1099)

float hash13(vec3 p3)
{
	p3  = fract(p3 * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 pushElement(in vec3 v, float n) {
	v.yz = v.xy;
    v.x = n;
    return v;
}

float getAtIdx(vec3 v, int i) {
 	if (i == 0) return v.x;
    else if (i == 1) return v.y;
    else if (i == 2) return v.z;
    else return 0.;
}

float keyPressed(int keyCode) {
	return texture(iChannel1, vec2((float(keyCode) + 0.5) / 256., .5/3.)).r;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    if (iFrame == 0 || bool(keyPressed(82))) {
        float hue = hash13(vec3(fragCoord, float(iFrame - 11)));
        float alive = step(hash13(vec3(fragCoord, float(iFrame))), 0.5);
        fragColor = vec4(alive, hue, 1.0, 1.0);
    }
    else {
        int n = 0;
        vec3 hues = vec3(0);
        for (float i = 0.; i < 8.; i += 1.) {
         	vec2 sampleOffset = vec2(cos(i * pi / 4.), sin(i * pi / 4.));
            sampleOffset *= 2.;
            sampleOffset = clamp(sampleOffset, -1., 1.);
            vec4 textel = texture(iChannel0, fract((fragCoord + sampleOffset) / iResolution.xy));
            if (textel.r > 0.5) {
            	hues = pushElement(hues, textel.g);
                n += 1;
            }
        }
        vec4 current = texture(iChannel0, fragCoord / iResolution.xy);
        if (current.r < 0.5 && n == 3) {
        	current.r = 1.;
            current.g = getAtIdx(hues, int(floor(mod(hash13(vec3(fragCoord, iTime) * 3. ), 3. ))));
        }
        else if (n < 2 || n > 3) current.r = 0.;
		fragColor = current;
    }
}
