float audio_freq( in sampler2D channel, in float f) { return texture( channel, vec2(f, 0.25) ).x; }
float audio_ampl( in sampler2D channel, in float t) { return texture( channel, vec2(t, 0.75) ).x; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    if (uv.x >= 0.5)
    {
        uv.x = 0.5-(uv.x-0.5);
    }

    //Final color
    vec4 col = vec4(.0,.0,.0,.0);

    //Sinewave
    float sa = (audio_ampl(iChannel0, uv.x)*2.0-1.0)*0.1;
    float ampl = .3;
    float freq = 1.0;
    freq = sa * 4.0;
    ampl = audio_freq(iChannel0, uv.x);
    uv.y = uv.y + sin(uv.x*freq)*ampl;

    //Chromatic aberration
    float ca = 0.02;
    ca *= (audio_freq(iChannel0, uv.x)*2.0-1.0)*5.0;
    col = 
        vec4
        (
       		texture(iChannel1, vec2(uv.x+ca, uv.y)).r,
            texture(iChannel1, vec2(uv.x, uv.y)).g,
            texture(iChannel1, vec2(uv.x-ca, uv.y)).b,
            1.0
        );

	fragColor = col;
}
