#define pi 3.14159265358979

vec3 ellipse(vec2 uv, vec3 baseCol, vec3 col, vec2 c, vec2 axes, float thickness){

    //thickness*=sin(iTime)+1.1;

    float eq = pow(uv.x - c.x,2.)/pow(axes.x,2.) + pow(uv.y - c.y,2.)/pow(axes.y,2.);
	float dist = abs(1.-eq);


    col += smoothstep(thickness,0.,dist) * baseCol;

    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);
    vec3 baseCol = vec3(.5*(cos(iTime)+1.)/2.,.5*(cos(iTime/2.)+1.)/2.,.5*(cos(iTime)+1.)/2.);

    for (float i = 0.; i < 314.;i++){

        col = ellipse(uv, baseCol, col,vec2(cos(iTime+i*.06),-.85+1.6*(sin(iTime+i*.08)+1.)/2.),

                      vec2(.1,.05),.45);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
