#define SC(a) vec2(cos(a),sin(a))

void mainImage( out vec4 o, vec2 fragCoord )
{
    vec2  R = iResolution.xy,
         uv = ( fragCoord -.5*R ) / R.y;

    float  t = iTime * ( 2.+.02* cos(4.*iTime) ),
         pi2 = 6.283,
          ft = mod(floor(2.*iTime),12.);

    for (
          float i=0., hex=-1.;
          i++ <= ft;
          hex = max ( hex ,
                      .3*cos(t)*1.8 - 505.
                       + 20. * length( uv-25.*SC(t+pi2/ft*i) ) 
                     ) 
        )
            o = vec4(3.-3.*hex); 
}





