vec2 mainSound( in int samp, float time )
{
    float t=mod(.5*time,6.);
    float scale=pow(9./8.,fract(4.*t));
    scale/=scale;

    return (.5+.4*float(fract(t*.25)<.5))*.3* vec2(  smoothstep(0.,1.,sin(acos(-1.)
            *72.0*scale     *t))*exp(-5.0*fract(t*4.)) );
}
