void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    uv-=0.5;
    uv*=1.2;
    float e = 0.2;
    float angle = sin(uv.x+uv.y)*17.0;
    vec2 p = vec2(0.5);
    p.x+=sin(angle)*e;
    p.y+=cos(angle)*e;
    float d = distance(p,mod(iTime*0.5+uv*7.0,1.0)) * 2.0;
	float big = smoothstep(d*0.9,d,0.5);
    float dd = distance(p,mod(iTime*-0.8+uv*5.0,1.0)) * 2.0;
	float small = smoothstep(dd*0.9,dd,0.1);
    fragColor = vec4(big+small);
}
