/**
//225 chars
void mainImage(out vec4 O, vec2 U )
{
    U /= iResolution.xy * .01;
    vec2 i = floor(U);
    U-=i; // fract
    U *= U * (3. - 2. * U); // smoothstep

    O += dot(
        fract(
            sin(
                vec2(12.9898, 78.233)
                	* mat4x2(
        		   		i, 
                  	 	i + O.wx,
                  	 	i + O.xw,
                   		i + 1.))*43758.5453123),
            vec4(1.- U.x - U.y, U.xy, 0.)
             + U.x * U.y *(1.-2.*O.xwwx)
                );
}
