    O =  0.*O +  dot(fract(sin(vec2(12.9898,78.233) * mat4x2(
        		   i, 
                   i + O.wx,
                   i + O.xw,
                   i + 1.))*43758.5453123),
                vec4(
                    1.- f.x - f.y + f.x * f.y,
        	 		    f.x       - f.x * f.y,
