               O, 		//vec4(-1.,  1.,  0., 0.);
               O.xwyw, //vec4(-1.,  0.,  1., 0.);
               O.yxxy) //vec4( 1., -1., -1., 1.);
        * vec4(1., f.xy, f.x * f.y)
     );
