/**
// 288 char
	void mainImage(out vec4 O, vec2 U )
{
    U = U/iResolution.xy * 1e2;
    vec2 f = floor(U);
    vec4 A = vec2(12.9898,78.233) * mat4x2(
        		   f, 
                   f + O.wx,
                   f + O.xw,
                   f + 1.);
    A = fract(sin(A)*43758.5453123);

    f = fract(U);
    f = f * f * (3. - 2. * f);
	vec3 s = vec3(0., 1., -1.);
    vec4 v = vec4(1., f.xy, f.x*f.y);
    O +=    dot(A,
                	mat4x4(s.yxxx, s.zyxx,s.zxyx , s.yzzy) *v
                );
}
