renderpass.code:
struct Rect {
	vec2 pos,
         ext;
    mat2 kord;
    vec4 col;

};


Rect 
     R1 = Rect ( vec2(.0,.5-4.*(iTime*iTime)), vec2 (1.,1.), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.));




mat2 Rota (in float alpha){
	float s = sin(alpha),
          c = cos(alpha);

   return mat2 (c, -s, s, c);

}


void Rotarect (in float alpha, inout Rect r_p){

    r_p.kord = Rota (alpha) * r_p.kord;


}



vec4 intersec (in vec2 p_uv, in Rect reco){
	vec2 xynew = p_uv - reco.pos;
    vec2 mat = reco.kord * xynew;

    if ( abs(mat.x) >.5*reco.ext.x || abs(mat.y) >.5*reco.ext.y){
        return vec4 (0.);
    }

    	if (mat.x*mat.x*reco.ext.x +mat.y*mat.y*reco.ext.y <.025)
    	return reco.col;


    return vec4(0.);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.*(fragCoord.xy / iResolution.xy-0.5)*vec2 ( iResolution.x/iResolution.y, 1.);
	fragColor = vec4(0.);

	Rotarect (iTime, R1);

	if (R1.pos.y <-.3)
        R1.pos.y = -.3+(4./(3.*iTime+.5))*(iTime-.5);


    vec4 r1col = intersec (uv,R1) ;
    if (r1col.a < 1.){
        fragColor = vec4(.5,.25,.75,.1);}
    else   
   fragColor = intersec (uv,R1) ;


}
