void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float blurry = sin(iTime)*0.25+0.25;
	vec2 uv = (2.*fragCoord.xy - iResolution.xy) / iResolution.y;
    float rad = ((atan(uv.x,uv.y)*0.31830988618379067153776752674503)*0.5)+0.5;
    float d = length(uv);
    float pattern  = abs(sin((rad)*6.0*3.141592653))*d;
    float re = d + (0.26*pattern)*(0.5-blurry);
    float r = ((cos(-blurry*20.0+d*60.0*(re-0.25))*0.25)+0.5);
    float g = ((cos(-0.25-blurry*20.0+d*70.0*(re-0.25))*0.25)+0.5);
    float b = ((cos(-0.5-blurry*20.0+d*80.0*(re-0.25))*0.25)+0.5);
	fragColor = 0.6*vec4(smoothstep(0.5+blurry,0.48-blurry,re));

    vec4 diffract = vec4(r,g,b,1.0)*(clamp((0.3-abs(0.5-d))*3.0,0.0,1.0))*0.1;
	fragColor = 2.0*(diffract*1.0*blurry+(0.4-blurry*0.1)*vec4(smoothstep(0.5+blurry,0.48-blurry,re)));
}
