vec3 HSLtoRGB(in float h, in float s, in float l)
{
  vec3 rgb = clamp( abs(mod(h+vec3(0.,4.,2.),6.)-3.)-1., 0., 1. );
  return l + s * (rgb-.5)*(1.-abs(2.*l-1.));
}

void mainImage(out vec4 o, vec2 i)
{
	i += i - (o.xy = iResolution.xy);
    float a = length(i /= (o.y>o.x?o.x:o.y)), b = length(i + 0.35), c = length(i - 0.35);
    o = vec4(a > 1. || b < .15 || (c < .5 || i.x > i.y) && c > .15 && b > .5 && a < .99);

    float h = 3.0*(sin(iTime)/35.+.5*(o.y>o.x?o.x:o.y));
    float s = 1.0;
    float l = 0.5*(cos(iTime)/35.+0.5);
    vec3 w = HSLtoRGB(h,s,l);
    w += vec3(1.0)-a;
    o /= vec4(w,0.);

    h = 0.125*(cos(iTime)/17.5+.125*(o.y>o.x?o.x:o.y));
    s = 1.;
    l = 1.0*(sin(iTime)/17.5+.125);
    w = HSLtoRGB(h,s,l);
    w += vec3(1.0)-b;
    o *= 1.0-vec4(w,0.);

    h = 0.125*(cos(iTime)/17.5+.125*(o.y>o.x?o.x:o.y));
    s = 1.;
    l = 1.0*(sin(iTime)/17.5+.125);
    w = HSLtoRGB(h,s,l);
    w += vec3(1.0)-c;
    o *= 1.0-vec4(w,0.);
}
