float ds(in float st, in float l0, in float l2){
  float l1 = (l0+l2)*0.5;
  return  smoothstep( l0, l1, st ) - smoothstep( l1, l2, st );
}

vec2 transUV(in vec2 uv, in vec2 ani){
    vec2 uvt = uv;
    uvt.x+=sin(uvt.x*26.0+ani.x)*0.02;
    uvt.y+=sin(uvt.y*22.0+ani.y)*0.02;
    return uvt;
}
vec2 transUV2(in vec2 uv, in vec2 uv2, in float val){
    vec2 uvt = uv;
    vec2 dir = normalize(uv - uv2);
    uvt+=dir*max(0.0,1.0-distance(uv,uv2))*val;
    //uvt.x+=sin(uvt.x*26.0+ani.x)*0.02;
    //uvt.y+=sin(uvt.y*22.0+ani.y)*0.02;
    return uvt;
}
