void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec2 uvM = iMouse.xy / iResolution.xy;

    // Time varying gradient
    vec3 col = 0.5 + 0.5*cos(uv.xyx*4.0+vec3(0,2,4));
	//col = fract((uv.x+uv.y)*100.0) < 0.5 ? vec3(1.0,1.0,1.0) : vec3(0.0,0.0,0.0);

    // Grid
    //vec2 uvt = transUV(uv,vec2(iTime*0.6));
    vec2 uvt = transUV2(uv,uvM,0.1+(sin(iTime*3.0)*0.5+0.5)*0.3);


   	float zebra1 = ds(fract((1.0-uvt.x+uvt.y)*20.0),0.0,0.8);
    float zebra2 = ds(fract((uvt.x+uvt.y)*20.0),0.0,0.8);
    float zebra = clamp(zebra1+zebra2,0.0,1.0);
    //vec3 colZebra = vec3(zebra2);

    float countX=20.0;
    float countY=12.0;
    float width = 0.1;
    float frakX =fract(uvt.x*countX);
    float frakY =fract(uvt.y*countY);
    float gridX = ds(frakX,0.0,width);
    float gridY = ds(frakY,0.0,width);

    vec3 finalCol = col*zebra;// + vec3(gridX*0.3)+ vec3(gridY*0.6);

    // Output to screen
    fragColor = vec4(finalCol,1.0);
}
