void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0, uv);
}

//This is what I learned:
//Buffers are like textures that you can change :) i like that
//Buffers can refer to themselves
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;
    if(iMouse.xy!=vec2(0.)){ // if its next to the mouse
        if (length(uv-mouse)<0.05){
            col = vec3(1.); //change buffer picture to white
        }
    } else {
        col = texture(iChannel0, uv).rgb;//otherwise, keep it the way it was
    }



    fragColor = vec4(col, 1.0);
}
