void mainImage( out vec4 O, vec2 U )
{
    U = floor(U/6.);
    O.g = T(U+vec2(0,139)).x;
    O.r = T(U+vec2(0,1)).x;
    if (U.y==2.) O.b = mod(U.x,2.), O.g = float(mod(U.x,10.)==9.);
}

void mainImage( out vec4 O, vec2 U )
{
    O = T(U);
    ivec2 I = ivec2(U);
    if (I.x>=500 || iFrame>500) return;
    if ( I.x+500*(I.y-140)==iFrame ) O.xy = iChannelResolution[1].xy;
    if ( I.x+500*(I.y-  1)==iFrame ) O.xy = vec2(textureSize(iChannel1,0));
}
